import React, { useState, useContext } from 'react';
import * as S from './styled.js';

let corpo = document.body;
corpo.style.background = '#aa7b5d';
corpo.style.color = '#FFFFFF';

export default function Main() {
  const [task, setTask] = useState('');
  const [list, setList] = useState([]);

  const itemTask = {
    task: task,
    id: Date.now(),
  };

  const handleSubmit = (e) => {
    setTask(e.preventDefault());
  };

  const onChange = event => {
   const Tarefa = localStorage.setItem('itemTask', event.target.value);
    setTask(event.target.value);
  };

  const remove = (id) => {
    setList(
      list.filter((item) => item.id != id),
      []
    );
  };

  const handleClick = () => {
    if(task !== ""){
      setList([...list, itemTask]), setTask('');
    }
    
  };

  return (
    <S.Container>
      <S.SubContainer onSubmit={(e) => {handleSubmit(e)}}>
        <input type="text" value={task} placeholder="digite aqui sua tarefa" onChange={onChange}/>
        <S.BtnADD onClick={handleClick}>Add Task</S.BtnADD>
        <S.List>
          {list.map((item, index) => (
            <>
              <ul key={index}>
                <li>{item.task}</li>
                <button onClick={() => remove(item.id)}>x</button>
              </ul>
            </>
          ))}
        </S.List>
      </S.SubContainer>
    </S.Container>
  );
}
